import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import headerImg from "../assets/img/header-img.svg";
import { ArrowRightCircle } from 'react-bootstrap-icons';
import TrackVisibility from 'react-on-screen';
import "./banner.css";

export const Banner = () => {
    const [loopNum, setLoopNum] = useState(0);
    const [isDeleting, setIsDeleting] = useState(false);
    const [text, setText] = useState('');
    const [delta, setDelta] = useState(300 - Math.random() * 100);
    const [isDark, setIsDark] = useState(false);

    useEffect(() => {
        let ticker = setInterval(() => {
            tick();
        }, delta);

        const handleScroll = () => {
            if (window.scrollY > 50) {
                setIsDark(true); // Set state to indicate background should be dark
            } else {
                setIsDark(false); // Set state to indicate background should be light
            }
        };

        document.addEventListener('scroll', handleScroll); // Add scroll event listener

        return () => {
            clearInterval(ticker);
            document.removeEventListener('scroll', handleScroll); // Remove scroll event listener on component unmount
        };
    }, [text]);

    const tick = () => {
        let i = loopNum % toRotate.length;
        let fullText = toRotate[i];
        let updatedText = isDeleting ? fullText.substring(0, text.length - 1) : fullText.substring(0, text.length + 1);

        setText(updatedText);

        if (isDeleting) {
            setDelta(prevDelta => prevDelta / 2);
        }

        if (!isDeleting && updatedText === fullText) {
            setIsDeleting(true);
            setDelta(period);
        } else if (isDeleting && updatedText === '') {
            setIsDeleting(false);
            setLoopNum(loopNum + 1);
            setDelta(500);
        }
    }

    const handleConnectClick = () => {
        console.log('Connect button clicked');
        // Add your connect functionality here
    }

    return (
        <section className={`banner ${isDark ? 'dark-bg' : ''}`} id="home">
            <Container>
                <Row className="align-items-center">
                    <Col xs={12} md={6} xl={7}>
                        <TrackVisibility>
                            {({ isVisible }) => (
                                <div className={isVisible ? "animated fadeInLeft" : ""}>
                                    <span className="tagline">Welcome to my Portfolio</span>
                                    <h1>
                                        <span>This is Daniel</span>
                                        <br />
                                        <span className="smaller-text">And I am a</span>{" "}
                                        <span className="highlighted-text">
                                            <span className="txt-rotate">
                                                <span className="wrap">{text}</span>
                                            </span>
                                        </span>
                                    </h1>
                                    <p>An passionate, self-motivated individual with strong skills in web development, penetration testing, Vulnerability Assessment, and some knowledge in networking, as well as a curious cat who enjoys learning new things.</p>
                                    <button onClick={handleConnectClick}>Let’s Connect <ArrowRightCircle size={25} /></button>
                                </div>
                            )}
                        </TrackVisibility>
                    </Col>
                    <Col xs={12} md={6} xl={5}>
                        <TrackVisibility>
                            {({ isVisible }) => (
                                <div className={isVisible ? "animated fadeInRight" : ""}>
                                    <img src={headerImg} alt="Header Img" />
                                </div>
                            )}
                        </TrackVisibility>
                    </Col>
                </Row>
            </Container>
        </section>
    );
}
