import { Container, Row, Col, Tab } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";
import projImg1 from "../assets/img/she-wallet.png";
import projImg2 from "../assets/img/project-img2.png";
import projImg3 from "../assets/img/project-img3.png";
import colorSharp2 from "../assets/img/color-sharp2.png";
import 'animate.css';
import "./Projects.css";
import TrackVisibility from 'react-on-screen';

export const Projects = () => {
    const projects = [
        {
            title: "Business Startup",
            description: "Design & Development",
            imgUrl: projImg1,
        },
        {
            title: "Business Startup",
            description: "Design & Development",
            imgUrl: projImg2,
        },
        {
            title: "Business Startup",
            description: "Design & Development",
            imgUrl: projImg3,
        },
        {
            title: "Business Startup",
            description: "Design & Development",
            imgUrl: projImg1,
        },
        {
            title: "Business Startup",
            description: "Design & Development",
            imgUrl: projImg2,
        },
        {
            title: "Business Startup",
            description: "Design & Development",
            imgUrl: projImg3,
        },
    ];

    // Define custom cursor styles
    const cursorStyle = {
        position: 'absolute',
        width: '20px',
        height: '20px',
        backgroundColor: 'transparent',
        border: '2px solid #4caf50',
        borderRadius: '50%',
        zIndex: '9999',
        pointerEvents: 'none',
        transition: 'border-color 0.3s ease',
    };

    // Track mouse movement to update cursor position
    const handleMouseMove = (e) => {
        const cursor = document.getElementById('custom-cursor');
        cursor.style.top = e.pageY + 'px';
        cursor.style.left = e.pageX + 'px';
    };

    return (
        <section className="project" id="projects" onMouseMove={handleMouseMove}>
            <div className="justify-content-center" > <h2 className="skill-heading">Projects</h2> </div>
            <Container>

                <Row>
                    <Col size={12}>
                        <TrackVisibility>
                            {({ isVisible }) =>
                                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>

                                    <p>The following projects provide actual samples of my work that highlight my abilities and experience. Every project has a brief description, links to live demos, and code repositories. It displays my aptitude for handling challenging issues, utilizing a variety of technologies, and successfully managing projects.</p>
                                    <Tab.Container id="projects-tabs" defaultActiveKey="first">
                                        <Tab.Content id="slideInUp" className={isVisible ? "animate__animated animate__slideInUp" : ""}>
                                            <Tab.Pane eventKey="first">
                                                <Row>
                                                    {
                                                        projects.map((project, index) => {
                                                            return (
                                                                <ProjectCard
                                                                    key={index}
                                                                    {...project}
                                                                />
                                                            )
                                                        })
                                                    }
                                                </Row>
                                            </Tab.Pane>
                                        </Tab.Content>
                                    </Tab.Container>
                                </div>}
                        </TrackVisibility>
                    </Col>
                </Row>
            </Container>
            {/* Render custom cursor */}
            <div id="custom-cursor" style={cursorStyle}></div>
            <img className="background-image-right" src={colorSharp2} alt="Background Image"></img>
        </section>
    )
}
